import React from 'react'; 
import Modal from 'react-modal';
import { useSelector, useDispatch } from 'react-redux';
import { addNewPerson } from '../../actions/people';

import { closeModal } from '../../actions/ui';
import { useForm } from '../../hooks/useForm';


Modal.setAppElement('#root');

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
        width: '70%',
    },
}

export const AdoptModal = () => {

    const dispatch = useDispatch();

    const { modal } = useSelector( state => state.ui );

    const [ formValues, handleInputChange, reset ] = useForm({
        fullname: 'Alejandro Vásquez', 
        birthday: '', 
        person: 'Janeth Osorio', 
        adopt: '', 
    }); 

    const { fullname, birthday, person, adopt } = formValues; 

    const handleCloseModal = () => {
        dispatch( closeModal() )
    }

    const handleSubmitNewPerson = (e) => {
        e.preventDefault()
        dispatch( addNewPerson(formValues) ); 
    }


    return (
        <Modal
            isOpen={ modal }
            // onAfterOpen={afterOpenModal}
            // onRequestClose={closeModal}
            //style={customStyles}
            contentLabel="Example Modal"
        >

                
            <div 
                className="main-modal"
            >
                <div className="title-modal">
                    <h1> New Person </h1>  
                </div>
                <button
                    className="btn btn-outline-danger btn-block ms-3 mt-2"
                    onClick={ handleCloseModal }
                >
                    <i className="fas fa-times"></i>
                </button>
                
                <hr />
            </div>

            <form 
                className="container"
                onSubmit={ handleSubmitNewPerson }
            >

                <div className="form-group mt-3">
                    <label className="mb-2">FullName: </label>
                    <input 
                        type="text"
                        className="form-control" 
                        name="fullname"  
                        onChange={ handleInputChange }
                        value={ fullname }
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Birthday: </label>
                    <input 
                        type="date" 
                        className="form-control" 
                        name="birthday" 
                        onChange={ handleInputChange }
                        value={ birthday }
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Person: </label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="person" 
                        onChange={ handleInputChange }
                        value={ person }
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Adopt: </label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="adopt" 
                        onChange={ handleInputChange }
                        value={ adopt }
                    />
                </div>

                <button
                    type="submit"
                    className="btn btn-outline-primary btn-block mt-3"
                >
                    <i className="far fa-save"></i>
                    <span> Guardar</span>
                </button>
                <button
                    className="btn btn-outline-secundary btn-block mt-3"
                    onClick={ reset }
                >
                    <span> Reset</span>
                </button>  
            </form>

            
        </Modal>
    )
}
