import React, { useEffect, useState } from 'react'; 
import Modal from 'react-modal';
import moment from 'moment';
import { useSelector, useDispatch } from 'react-redux';

import { editPerson, noActivePerson, startAddNewPeople, startEditPerson } from '../../actions/people';
import { closeModal } from '../../actions/ui';
import { customStyles } from '../../helpers/modalStyles';


Modal.setAppElement('#root');

const initialState = {
    id: new Date().getMilliseconds(), 
    fullname: 'Alejandro Vásquez O.', 
    birthday: '1995-11-22', 
    person: '1', 
    adopt: '1', 
}
export const AdoptModal = () => {

    const dispatch = useDispatch();

    const { modal } = useSelector( state => state.ui );
    const { activePerson } = useSelector( state => state.pe );

    const [ formValues, setFormValues ] = useState(initialState); 

    const { fullname, birthday, person, adopt } = formValues; 

    const birth = birthday; 

    // useEffect(() => {

    //     if ( activePerson ) {
    //         setFormValues( activePerson ); 
    //     } else {
    //         setFormValues( initialState ); 
    //     }

    // }, [activePerson, setFormValues])

    const reset = () => {
        setFormValues( initialState );
    }
    const handleDateChange = ({target}) => {

        console.log(moment(target.value).format('L'))

        setFormValues({
            ...formValues, 
            birthday: moment(target.value).format('L') 
        });
    }

    const handleInputChange = ({ target }) => {
        setFormValues({
            ...formValues, 
            [target.name]: target.value
        });
    }
    
    const handleCloseModal = () => {
        dispatch( closeModal() )
        dispatch( noActivePerson() ); 
        setFormValues( initialState ); 
    }
    
    const handleSubmitNewPerson = (e) => {

        e.preventDefault()
        console.log(formValues); 
        dispatch( startAddNewPeople(formValues) ); 
        
        // if(activePerson){
        //     dispatch( editPerson(formValues) ); 
        // }else {
        // }

        closeModal(); 
    }


    return (
        <Modal
            isOpen={ modal }
            // onAfterOpen={afterOpenModal}
            onRequestClose={closeModal}
            style={customStyles}
            closeTimeoutMS={ 200 }
            contentLabel="Example Modal"
        >

                
            <div 
                className="main-modal"
            >
                <div className="title-modal">
                    <h1>{ (activePerson.length === 0) ? 'New person' : 'Edit person' }</h1>  
                </div>
                <button
                    className="btn btn-outline-danger btn-block ms-3 mt-2"
                    onClick={ handleCloseModal }
                >
                    <i className="fas fa-times"></i>
                </button>
                
                <hr />
            </div>

            <form 
                className="container"
                onSubmit={ handleSubmitNewPerson }
            >

                <div className="form-group mt-3">
                    <label className="mb-2">FullName: </label>
                    <input 
                        type="text"
                        className="form-control" 
                        name="fullname"  
                        onChange={ handleInputChange }
                        value={ fullname }
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Birthday: </label>
                    <input 
                        type="date"
                        className="form-control" 
                        name="birthday" 
                        onChange={ handleDateChange }
                        value={ birthday }
                        format="y-MM-dd"
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Person: </label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="person" 
                        onChange={ handleInputChange }
                        value={ person }
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Adopt: </label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="adopt" 
                        onChange={ handleInputChange }
                        value={ adopt }
                    />
                </div>

                <button
                    type="submit"
                    className="btn btn-outline-primary btn-block mt-3"
                >
                    <i className="far fa-save"></i>
                    <span> Guardar</span>
                </button>
                <button
                    className="btn btn-outline-secundary btn-block mt-3"
                    onClick={ reset }
                >
                    <span> Reset</span>
                </button>  
            </form>

            
        </Modal>
    )
}
