import { types } from "../types/types";

export const uiReducer = ( state= {}, action ) => {

    switch (action.type) {
        case types.uiOpenModal:
            return {
                ...state,
                modal: true
            }
        case types.uiCloseModal:
            return {
                ...state, 
                modal: false
            }
        default:
            return state
    }

}
