import moment from 'moment';  

export const preparePerson =  ( people = [] )=> {

    const dates = people.map( x => x.birthday ); 
    console.log(dates); 

    return (
        people.reduce( (acc, el) => acc.concat(moment(el).format('L')), [])
    )
}
