import React, { useContext } from 'react'
import Modal from 'react-modal'

import { PeopleContext } from '../../actions/PeopleContext';
import { closeModal } from '../../actions/ui';
import { useForm } from '../../hooks/useForm';


Modal.setAppElement('#root');

const customStyles = {
    content: {
        top: '50%',
        left: '50%',
        right: 'auto',
        bottom: 'auto',
        marginRight: '-50%',
        transform: 'translate(-50%, -50%)',
        width: '70%',
    },
}

export const AdoptModal = () => {

    const { state, dispatch } = useContext( PeopleContext ); 

    const { modal } = state.ui; 

    const [ formValues, handleInputChange ] = useForm({
        fullname: 'Alejandro Vásquez', 
        birthday: '22/11/1995', 
        person: 1, 
        adopt: 1, 
    }); 

    const { fullname, birthday, person, adopt } = formValues; 

    const handleCloseModal = () => {
        dispatch( closeModal() )
    }

    const handleSubmitNewPerson = (e) => {{
        e.preventDefault()
        console.log(formValues)
    }}


    return (
        <Modal
            isOpen={ modal }
            // onAfterOpen={afterOpenModal}
            // onRequestClose={closeModal}
            style={customStyles}
            contentLabel="Example Modal"
        >

                
            <div 
                className="main-modal"
            >
                <div className="title-modal">
                    <h1> New Person </h1>  
                </div>
                <button
                    className="btn btn-outline-danger btn-block ms-3 mt-2"
                    onClick={ handleCloseModal }
                >
                    <i className="fas fa-times"></i>
                </button>
                
                <hr />
            </div>

            <form 
                className="container"
                onSubmit={ handleSubmitNewPerson }
            >

                <div className="form-group mt-3">
                    <label className="mb-2">FullName: </label>
                    <input 
                        type="text"
                        className="form-control" 
                        name="fullname"  
                        onChange={ handleInputChange }
                        value={ fullname }
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Birthday: </label>
                    <input 
                        type="date" 
                        className="form-control" 
                        name="birthday" 
                        onChange={ handleInputChange }
                        value={ birthday }
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Person: </label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="person" 
                        onChange={ handleInputChange }
                        value={ person }
                    />
                </div>

                <div className="form-group mt-3">
                    <label className="mb-2">Adopt: </label>
                    <input 
                        type="text" 
                        className="form-control" 
                        name="apopt" 
                        onChange={ handleInputChange }
                        value={ adopt }
                    />
                </div>

                <button
                    type="submit"
                    className="btn btn-outline-primary btn-block mt-3"
                >
                    <i className="far fa-save"></i>
                    <span> Guardar</span>
                </button>
                <button
                    className="btn btn-outline-secundary btn-block mt-3"
                >
                    <span> Reset</span>
                </button>  
            </form>

            
        </Modal>
    )
}
